import axios from 'axios';

const api = axios.create({
    baseURL: 'http://localhost:5055/api', 
    headers: {
        'Content-Type': 'application/json'
    }
});

// ========== TOURS ==========
export const getTours = (status) => {const url = status ? `/tours?status=${status}` : "/tours";return api.get(url);};
export const getTourById = (id) => api.get(`/tours/${id}`);
export const createTour = (data) => api.post('/tours', data);
export const updateTour = (id, data) => api.put(`/tours/${id}`, data);
export const deleteTour = (id) => api.delete(`/tours/${id}`);

// ========== BOOKINGS ==========
export const getBookings = () => api.get('/bookings');
export const createBooking = (data) => api.post('/bookings', data);
export const updateBookingStatus = (id, data) => api.patch(`/bookings/${id}`, data);

// ========== USERS ==========
export const getUsers = () => api.get('/users');
export const loginUser = (credentials) => api.post('/users/login', credentials); // ?? ???? login API

// ========== TOUR REQUESTS ==========
export const getTourRequests = () => api.get('/tourrequests');
export const createTourRequest = (data) => api.post('/tourrequests', data);
export const approveTourRequest = (id) => api.patch(`/tourrequests/${id}`, { status: 'approved' });
export const rejectTourRequest = (id) => api.patch(`/tourrequests/${id}`, { status: 'rejected' });

// ========== COMPLAINTS ==========
export const getComplaints = () => api.get('/complaints');
export const createComplaint = (data) => api.post('/complaints', data);

export default api;
